var addresses = [0];
var socket;

function initSocket() {
    socket = new WebSocket("wss://ws.blockchain.info/inv");
    socket.onerror = function(e) {
        $(".log").append('<p class="error">Error...try again later.</p>');
    };
    socket.onopen = function() {
        $(".log").append('<p class="success">Success! Connection established.');
        socket.send(JSON.stringify({"op": "blocks_sub"}));
    };

    socket.onmessage = function(m) {
        beep();
        var data = JSON.parse(m.data);
        if (data.op == "block") {
            $(".log").append("<p>Block #" + Math.round(data.x.height) + " found.</p>");
        }
        else if (data.op == "utx") {
            for (var i = 0; i < data.x.out.length; i++) {
                var amt = data.x.out[i].value / 100000000;
                var addy = data.x.out[i].addr;
                for (var j = 0; j < addresses.length; j++) {
                    if (addresses[j] == addy) {
                        $(".log").append("<p>" + amt + "BTC received to " + addy);
                    }
                }
            }
        }
    };
}

function beep() {
    var snd = new Audio("data:audio/wav;base64,");  
    snd.play();
}

$(document).ready(function() {
    initSocket();
    $(".addmore").on("click", function() {
        addresses.push(0);
        $("#boxes").append('<div class="input" id="' + (addresses.length) + '"><input class="address" id="address" placeholder="address here" type="text" title="A valid Bitcoin address should go here."><div class="status" id="button"><p id="status">disconnected</p><p class="click">click to connect</p></div></div>')
    });

    $("#boxes").on("click", "#button", function() {
        var address = $(this).parent().find("#address").val();
        if (typeof address != "undefined" && address.length > 10) {
            if ($(this).find(".click").text() == "click to connect") {
                $(this).find(".click").text("click to disconnect");
                $(this).find("#status").text(" connected ");
                $(this).find("#status").css("color", "green");
                socket.send(JSON.stringify({ "op": "addr_sub", "addr": address }));
            }
            else {
                $(this).find(".click").text("click to connect");
                $(this).find("#status").text("disconnected");
                $(this).find("#status").css("color", "red");
                socket.send(JSON.stringify({ "op": "addr_unsub", "addr": address }));
            }
        }
        else {
            $(".log").append("<p>Enter a valid address.</p>");
        }
    });

    var timer;
    $("#boxes").on("keyup", "#address", function(e) {
        clearTimeout(timer);
        if ($(this).val()) {
            timer = setTimeout(done(e), 2500);
        }

        function done(el) {
            var address = $(el.target).val();
            console.log($(el).parent().attr("id"));
            addresses[$(el.target).parent().attr("id") - 1] = address;
        }
    });
    
    $(".log").bind("DOMSubtreeModified", function() {
        $(this).scrollTop($(this)[0].scrollHeight);
    });
});